package com.example.a1lesson4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.a1lesson4.databinding.FragmentFilmBinding;

import java.util.ArrayList;

public class FilmFragment extends Fragment implements OnItemClickListener{

    private FragmentFilmBinding binding;
    private FilmAdapter adapter;
    private ArrayList<Film> filmList = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentFilmBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        adapter = new FilmAdapter(filmList,this);
        binding.rvFilm.setAdapter(adapter);
    }

    private void loadData() {
        filmList.add(new Film("Africa","data:image/png;base64,"));
        filmList.add(new Film("N. America","data:image/png;base64,"));
        filmList.add(new Film("S. America","data:image/png;base64,"));
        filmList.add(new Film("Asia","data:image/png;base64,"));
        filmList.add(new Film("Europe","data:image/jpeg;base64,"));
    }

    @Override
    public void onItemClick(int position) {
        Film film = filmList.get(position);
        Bundle bundle = new Bundle();
        bundle.putSerializable("film", film);
        DetailFragment fragment = new DetailFragment();
        fragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction()
                .replace(R.id.container,fragment).addToBackStack(null).commit();
    }
}